if ( ! defined( 'ABSPATH' ) ) {
	exit;
}

/**
 * A class to call Whoop assets.
 *
 * We call these statically so they can easily be removed by 3rd party devs.
 *
 * Class Whoop_Assets
 */
class Whoop_Geodirectory_Content {


	/**
	 * Init
	 */
	public static function init() {
		add_filter( 'geodir_wizard_content', array( __CLASS__, 'add_sections' ) );
		add_action( 'geodir_wizard_content_whoop_content', array( __CLASS__, 'wizard_settings' ) );

		add_action( 'wp_ajax_whoop_wizard_set_page_content',  array(__CLASS__,'ajax_set_page_content') );
		add_action( 'wp_ajax_whoop_wizard_set_rating_style',  array(__CLASS__,'ajax_set_rating_style') );
		add_action( 'wp_ajax_whoop_wizard_set_tabs_order',  array(__CLASS__,'ajax_set_tabs_order') );

		// set some default page contents
		add_filter("geodir_default_page_details_content", array( __CLASS__, 'page_details_content' ), 10, 2 );
		add_filter("geodir_default_page_archive_content", array( __CLASS__, 'page_archive_content' ), 10, 2 );
		add_filter("geodir_default_page_location_content", array( __CLASS__, 'page_location_content' ), 10, 2 );
		add_filter("geodir_default_page_search_content", array( __CLASS__, 'page_search_content' ), 10, 2 );
		add_filter("geodir_default_page_archive_item_content", array( __CLASS__, 'page_archive_item_content' ), 10, 2 );
//		add_filter("dt_header_extra_class", array( __CLASS__, 'header_class' ) );


		// dummy widget content
		add_filter('geodir_dummy_widgets',array( __CLASS__, 'dummy_widgets' ), 10, 2);
	}

	/**
	 * Adjust the header classes.
	 *
	 * @param $class
	 *
	 * @return mixed
	 */
	public static function header_class($class){

		if(geodir_is_page('single')){
			$class = str_replace( "border-bottom", "", $class );
		}

		return $class;
	}

	public static function dummy_widgets($widgets, $type){

		if($type=='top'){
			$widgets = array(
				// show map
				'gd_post_images' => array(
					'types' => 'post_images,comment_images',
					'ajax_load' => 'true',
					'limit_show' => '4',
					'type' => 'slider',
					'slideshow' => 'true',
					'controlnav' => '0',
					'animation' => 'slide',
					'show_title' => 'false',
					'show_caption' => 'false',
					'link_to' => 'lightbox',
					'gd_wgt_showhide'   => 'show_on',
					'gd_wgt_restrict'   => array('gd-detail'),
				)
			);
		}else{
			$widgets = array(
				// show the author action on the details sidebar
				'gd_author_actions' => array(
					'hide_edit'          => false,
					'hide_delete'          => false,
					'gd_wgt_showhide'   => 'show_on',
					'gd_wgt_restrict'   => array('gd-detail'),
				),
				// show details sidebar
				'gd_output_location' => array(
					'location'          => '[detail]',
					'gd_wgt_showhide'   => 'show_on',
					'gd_wgt_restrict'   => array('gd-detail'),
				),
				// show GD Dashboard
				'gd_dashboard' => array(
					'dashboard_title'   => __('GD Dashboard','whoop'),
					'show_login'        => true,
					'login_title'        => __('Login','whoop'),
					'gd_wgt_showhide'   => 'show',
					'gd_wgt_restrict'   => array('gd-author'),
				),

			);
		}

		return $widgets;
	}

