			wp_die( -1 );
		}

		$wpdb->update( GEODIR_TABS_LAYOUT_TABLE, array( 'sort_order' => 0), array( 'tab_key' => 'reviews' ) );

		wp_send_json_success(__("Reviews tab order updated successfully.","whoop"));

		wp_die();
	}

